import React from 'react';
//import axios from 'axios';

import styles from '../Main.module.css';
import taskStyles from './Task1.module.css';

class Task1 extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          isFetching: false
        };
      }

    submitHandle(event) {
        event.preventDefault();
        let form = event.target;
        //let data = new FormData(form);
        //data.delete('picture');
        let data = new FormData();
        console.log(event.target.elements.alphaStep.value);
        data.append('alphaStep', event.target.elements.alphaStep.value);
        data.append('count', event.target.elements.count.value);
        data.append('spread', event.target.elements.spread.value);
        data.append('picture', event.target.elements.picture.value)

        let c;
        //console.log(data.get('picture').webkitRelativePath)
        console.log(JSON.stringify(Object.fromEntries(data)));
        fetch('//localhost:8080/hi', {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: Object.fromEntries(data)
        })
        //.then(response => response.json())
        .then(result => {
          c = result.text().then((data) => {
            c = console.log('Success:', data);
          });
        })
        .catch(error => {
          console.error('Error:', error);
        });
    }

    render() {
        const fetchBlock = (this.state.isFetching) ?
            <div className={styles.loadingPane}> Fetching... </div>
            :null;

        const mainTask =
            <div className={taskStyles.taskMain}>
                <div className={taskStyles.inputFormDiv}>
                    <form onSubmit={this.submitHandle}>
                        <label htmlFor='picture'>Zamieść Obraz</label><br />
                        <input type="file" id="picture" name='picture' accept=".jpg, .jpeg, .png" /><br />
                        <label htmlFor='alphaStep'>Krok ∆α układu emiter/detektor</label><br />
                        <input type='number' id='alphaStep' name='alphaStep' /><br />
                        <label htmlFor='count'>Liczba detektorów</label><br />
                        <input type='number' id='count' name='count' /><br />
                        <label htmlFor='spread'>Rozwartość detektorów</label><br />
                        <input type='number' id='spread' name='spread' /><br />
                        <input type='submit' value='Generate' /><br />
                    </form>
                </div>
                <div className={taskStyles.imageView} id='inputImage'>
                </div>
                <div className={taskStyles.imageView} id='processImage'>
                </div>
                <div className={taskStyles.imageView} id='outputImage'>
                </div>
            </div>

        return (
            <main>
               {fetchBlock}
               {mainTask}
            </main>
        )}
}

export default Task1;
