import React from 'react';
//import axios from 'axios';

import styles from '../../Main.module.css';
import './fieldTable.css';

class FieldTable extends React.Component {

    tableElems = [];
    controlElems = [];

    addImg = './icoNotes/add.png';
    removeImg = './icoNotes/remove.png';
    arrowUpImg = './icoNotes/arrowUp.png';
    arrowDnImg = './icoNotes/arrowDn.png';
    arrowImg = './icoNotes/arrow.png';

    constructor(props){
        super(props);
        this.tableElems = [];
        this.tableElems.push(this.addEditableRow('Jill', 'Smith', '50'));
        this.tableElems.push(this.addEditableRow('Eve', 'Jackson', '94'));
    }

    componentDidMount() {
        let table = document.getElementById('editable');

        for (let row of this.tableElems){
            this.addControlRow();
        }

        let newRow = this.createAddRowIcon();
        document.getElementsByTagName('main')[0].appendChild(newRow);
    }

    addEditableRow(text1, text2, text3){
        return (
            <tr>
                {this.addEditableCell(text1)}
                {this.addEditableCell(text2)}
                {this.addEditableCell(text3)}
            </tr>
        )
    }

    addEditableCell(text){
        return(
            <td>
                <span onClick={this.editField}>{text}</span>
                <textarea type='text' onBlur={this.closeEditor} placeholder='lorem' className='noDisplay' />
            </td>
        )
    }

    addControlRow(){
        return(
            <tr>
                {this.addControlField(this.removeImg, 'rmIcon icon', 'removeRow(this.parentNode.parentNode)', 'remove')}
                {this.addControlField(this.arrowUpImg, 'icon', 'moveUp(this.parentNode.parentNode)', 'move up')}
                {this.addControlField(this.arrowDnImg, 'icon', 'moveDn(this.parentNode.parentNode)', 'move down')}
            </tr>
        )
    }

    addControlField(src, clsName, action, alt){
        return(
            <td>
                <img src={src} class={clsName} onClick={action} alt={alt} />
            </td>
        )
    }

    render() {
        let table =
            <table id='editable'>
                <tr>
                    <th>Firstname</th>
                    <th>Lastname</th>
                    <th>Age</th>
                </tr>
                {this.tableElems}
            </table>

        let controls =
            <table className='controls'>
                <tr>
                    <th className='empty' />
                    <th className='empty' />
                    <th className='empty' />
                </tr>
                {this.controlElems}
            </table>

        return (
            <div className='tableParent'>
                {table}
                {controls}
            </div>
        )
    }
}

export default FieldTable;
