import React from 'react';
//import axios from 'axios';

import styles from '../../Main.module.css';
import './fieldTable.css';

class FieldTable extends React.Component {

    tableElems = [];
    controlElems = [];
    keyCounter = 0;

    addImg = '/static/images/icoNotes/add.png';
    removeImg = '/static/images/icoNotes/remove.png';
    arrowUpImg = '/static/images/icoNotes/arrowUp.png';
    arrowDnImg = '/static/images/icoNotes/arrowDn.png';
    arrowImg = '/static/images/icoNotes/arrow.png';

    constructor(props){
        super(props);

        this.tableElems = [];
        this.tableElems.push(this.addEditableRow('Jill', 'Smith', '50'));
        this.tableElems.push(this.addEditableRow('Eve', 'Jackson', '94'));
    }

    componentDidMount() {
        for (let row of this.tableElems){
            this.addControlRow();
        }

    }

    addEditableRow(text1, text2, text3){
        this.keyCounter = this.keyCounter + 1;
        return (
            <tr key={this.keyCounter}>
                {this.addEditableCell(text1)}
                {this.addEditableCell(text2)}
                {this.addEditableCell(text3)}
            </tr>
        )
    }

    addEditableCell(text){
        this.keyCounter = this.keyCounter + 1;
        return(
            <td key={this.keyCounter}>
                <span onClick={this.editField}>{text}</span>
                <textarea type='text' onBlur={this.closeEditor} placeholder='lorem' className='noDisplay' />
            </td>
        )
    }

    addControlRow(){
        return(
            <tr>
                {this.addControlField(this.removeImg, 'rmIcon icon', 'removeRow(this.parentNode.parentNode)', 'remove')}
                {this.addControlField(this.arrowUpImg, 'icon', 'moveUp(this.parentNode.parentNode)', 'move up')}
                {this.addControlField(this.arrowDnImg, 'icon', 'moveDn(this.parentNode.parentNode)', 'move down')}
            </tr>
        )
    }

    addControlField(src, clsName, action, alt){
        return(
            <td>
                <img src={src} class={clsName} onClick={action} alt={alt} />
            </td>
        )
    }

    addRow = () => {
        this.tableElems.push(this.addEditableRow('Jil', 'Smith', '50'));
        this.forceUpdate();
    }

    editField = event => {
        console.log(event.target.textContent)
        event.target.classList.add('noDisplay');
        event.target.parentNode.lastElementChild.value = event.target.textContext;
        event.target.parentNode.lastElementChild.classList.remove('noDisplay');
    }

    render() {
        let table =
            <table id='editable'>
                <thead>
                    <tr>
                        <th>Firstname</th>
                        <th>Lastname</th>
                        <th>Age</th>
                    </tr>
                </thead>
                <tbody>
                    {this.tableElems}
                </tbody>
            </table>

        let controls =
            <table className='controls'>
                <thead>
                    <tr>
                        <th className='empty' />
                        <th className='empty' />
                        <th className='empty' />
                    </tr>
                </thead>
                <tbody>
                    {this.controlElems}
                </tbody>
            </table>

        let addRow =
            <div className='newRow'>
                <img src={this.addImg} className='addRow' onClick={this.addRow} />
            </div>

        return (
            <div className='tableParent'>
                {table}
                {controls}
                {addRow}
            </div>
        )
    }
}

export default FieldTable;
