import React from 'react';
//import axios from 'axios';

import styles from '../Main.module.css';
import taskStyles from './Task1.module.css';

class Task1 extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
          isFetching: false
        };
        this.ToBase64.bind(this);
    }

    ToBase64 = function (u8) {
      return btoa(String.fromCharCode.apply(null, u8));
    }

    FromBase64 = function (str) {
      return atob(str).split('').map(function (c) { return c.charCodeAt(0); });
    }

    submitHandle = event => {

        event.preventDefault();
        let form = event.target;
        //let data = new FormData(form);
        //data.delete('picture');
        let data = new FormData();

        data.append('alphaStep', event.target.elements.alphaStep.value);
        data.append('count', event.target.elements.count.value);
        data.append('spread', event.target.elements.spread.value);
        data.append('picture', event.target.elements.picture.files[0])

        let c;
        //console.log(event.target.elements.picture.files[0]);
        //console.log(JSON.stringify(Object.fromEntries(data)));

        fetch('//localhost:8080/hi', {
            method: 'PUT',
            //headers: { 'Content-Type': 'application/json' },
            //headers: { 'Content-Type': 'multipart/form-data' },
            body: data

        })
        //.then(response => response.json())
        .then((result) => {
            c = console.log('Success:', result);

//            const filename =  result.headers.get('Content-Disposition').split('filename=')[1];
//                  result.blob().then(blob => {
//                    let url = window.URL.createObjectURL(blob);
//                    let a = document.createElement('a');
//                    a.href = url;
//                    a.download = filename;
//                    a.click();
//            });
           //})

            let iterator = result.headers.entries();
            //let val = iterator.next();
            console.log(Array.from(iterator));

            result.formData()
            .then((formdata) => {
                console.log(Array.from(formdata.keys()));
                console.log(Array.from(formdata.values()));
                console.log(typeof formdata.get('file1'));
                let str = formdata.get('file1').slice(1, -1);
                let array = str.split(",").map(out => (parseInt(out, 10)));
                console.log(typeof array);


                document.getElementById("inputImage").src = "data:image/jpeg;base64," + this.ToBase64(array);
            })

        })
        .catch(error => {
          console.error('Error:', error);
        });

    }

    render() {
        const fetchBlock = (this.state.isFetching) ?
            <div className={styles.loadingPane}> Fetching... </div>
            :null;

        const mainTask =
            <div className={taskStyles.taskMain}>
                <div className={taskStyles.inputFormDiv}>
                    <form onSubmit={this.submitHandle}>
                        <label htmlFor='picture'>Zamieść Obraz</label><br />
                        <input type="file" id="picture" name='picture' accept=".jpg, .jpeg, .png" /><br />
                        <label htmlFor='alphaStep'>Krok ∆α układu emiter/detektor</label><br />
                        <input type='number' id='alphaStep' name='alphaStep' /><br />
                        <label htmlFor='count'>Liczba detektorów</label><br />
                        <input type='number' id='count' name='count' /><br />
                        <label htmlFor='spread'>Rozwartość detektorów</label><br />
                        <input type='number' id='spread' name='spread' /><br />
                        <input type='submit' value='Generate' /><br />
                    </form>
                </div>
                <div className={taskStyles.imageView0} id={taskStyles.inputImageDiv}>
                    <img id='inputImage' src="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=" alt="input" />
                </div>
                <div className={taskStyles.imageView1} id={taskStyles.processImageDiv}>
                    <img id='processImage' src="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=" alt="process" />
                </div>
                <div className={taskStyles.imageView2} id={taskStyles.outputImageDiv}>
                    <img id='outputImage' src="data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAABCAQAAAC1HAwCAAAAC0lEQVR42mNgYAAAAAMAASsJTYQAAAAASUVORK5CYII=" alt="output" />
                </div>
            </div>

        return (
            <main>
               {fetchBlock}
               {mainTask}
            </main>
        )}
}

export default Task1;
